import { precacheAndRoute } from 'workbox-precaching';
import { registerRoute } from 'workbox-routing';
import { StaleWhileRevalidate } from 'workbox-strategies';

self.__WB_MANIFEST = self.__WB_MANIFEST || [];

// Pre-cache the assets generated by the build process
precacheAndRoute(self.__WB_MANIFEST);

// Cache any other requests using a stale-while-revalidate strategy
registerRoute(
  ({ request }) => request.destination === 'document' || request.destination === 'script' || request.destination === 'style' || request.destination === 'image',
  new StaleWhileRevalidate()
);
